oute(MessageId msgId)\r\n    {\r\n        return false;\r\n    }\r\n};\r\n\r\nint main() \r\n{\r\n    int a = 5;\r\n    Routes&lt;0, 3, 5&gt;::route(a);  \r\n    return 0;\r\n}</pre>\r\n&nbsp;','Switch-if 语句的模板替换','','inherit','closed','closed','','82-revision-v1','','','2018-10-10 06:36:37','2018-10-10 06:36:37','',82,'http://45.77.202.181/?p=134',0,'revision','',0),(135,1,'2018-10-10 06:37:28','2018-10-10 06:37:28','<pre class=\"EnlighterJSRAW\" data-enlighter-language=\"null\">            \r\n#include &lt;iostream&gt;\r\n#include &lt;functional&gt;\r\n#include &lt;vector&gt;\r\n\r\nusing namespace std;\r\n\r\ntemplate&lt;int Id&gt;\r\nclass ID\r\n{\r\n};\r\n\r\nvoid fun(ID&lt;0&gt; id)\r\n    {\r\n        cout&lt;&lt;\"eeeee\"&lt;&lt;endl;\r\n    }\r\n    \r\n    void fun(ID&lt;3&gt; id)\r\n    {\r\n        cout&lt;&lt;\"33333\"&lt;&lt;endl;\r\n    }\r\n    \r\n    void fun(ID&lt;5&gt; id)\r\n    {\r\n        cout&lt;&lt;\"55555\"&lt;&lt;endl;\r\n    }\r\n    \r\n    using MessageId = unsigned int;\r\n    \r\ntemplate &lt;MessageId... routeIds&gt;\r\nclass Routes\r\n{\r\npublic:\r\n\r\n    static constexpr bool route(MessageId msgId)\r\n    {\r\n        constexpr MessageId currentId = unpackFirstId&lt;routeIds...&gt;();\r\n        if (currentId == msgId)\r\n        {\r\n            fun(ID&lt;currentId&gt;());\r\n            return true;\r\n        }        else\r\n        {\r\n            cout&lt;&lt;currentId&lt;&lt;\"!=\"&lt;&lt;msgId&lt;&lt;endl;\r\n        }\r\n   \r\n        return searchFurther&lt;routeIds...&gt;(msgId);\r\n    }\r\n\r\nprivate:\r\n    template &lt;MessageId skippedId, MessageId... remainingIds&gt;\r\n    static constexpr bool searchFurther(MessageId msgId)\r\n    {\r\n        return Routes&lt;remainingIds...&gt;::route(msgId);\r\n    }\r\n\r\n    template &lt;MessageId firstId, MessageId... remainingIds&gt;\r\n    static constexpr MessageId unpackFirstId()\r\n    {\r\n        return firstId;\r\n    }};\r\n\r\ntemplate &lt;&gt;\r\nstruct Routes&lt;&gt;\r\n{\r\n    static constexpr bool route(MessageId msgId)\r\n    {\r\n        return false;\r\n    }\r\n};\r\n\r\nint main() \r\n{\r\n    int a = 5;\r\n    Routes&lt;0, 3, 5&gt;::route(a);  \r\n    return 0;\r\n}</pre>\r\n&nbsp;','Switch-if 语句的模板替换','','inherit','closed','closed','','82-revision-v1','','','2018-10-10 06:37:28','2018-10-10 06:37:28','',82,'http://45.77.202.181/?p=135',0,'revision','',0),(136,1,'2018-10-10 06:40:13','2018-10-10 06:40:13','<pre class=\"EnlighterJSRAW\" data-enlighter-language=\"null\">                \n#include &lt;iostream&gt;\n#include &lt;functional&gt;\n#include &lt;vector&gt;\n\nusing namespace std;\n\ntemplate&lt;int Id&gt;\nclass ID\n{\n};\n\nvoid fun(ID&lt;0&gt; id)\n{\n    cout&lt;&lt;\"eeeee\"&lt;&lt;endl;\n}\n\nvoid fun(ID&lt;3&gt; id)\n{\n    cout&lt;&lt;\"33333\"&lt;&lt;endl;\n}\n\nvoid fun(ID&lt;5&gt; id)\n{\n    cout&lt;&lt;\"55555\"&lt;&lt;endl;\n}\n\nusing MessageId = unsigned int;\n    \ntemplate &lt;MessageId... routeIds&gt;\nclass Routes\n{\npublic:\n\n    static constexpr bool route(MessageId msgId)\n    {\n        constexpr MessageId currentId = unpackFirstId&lt;routeIds...&gt;();\n        if (currentId == msgId)\n        {\n            fun(ID&lt;currentId&gt;());\n            return true;\n        }\n        else\n        {\n            cout&lt;&lt;currentId&lt;&lt;\"!=\"&lt;&lt;msgId&lt;&lt;endl;\n        }\n   \n        return searchFurther&lt;routeIds...&gt;(msgId);\n    }\n\nprivate:\n    template &lt;MessageId skippedId, MessageId... remainingIds&gt;\n    static constexpr bool searchFurther(MessageId msgId)\n    {\n        return Routes&lt;remainingIds...&gt;::route(msgId);\n    }\n\n    template &lt;MessageId firstId, MessageId... remainingIds&gt;\n    static constexpr MessageId unpackFirstId()\n    {\n        return firstId;\n    }\n};\n\ntemplate &lt;&gt;\nstruct Routes&lt;&gt;\n{\n    static constexpr bool route(MessageId msgId)\n    {\n        return false;\n    }\n};\n\nint main() \n{\n    